3.创建 reducer 对象
// query_reducer.ts
import { CouponQueryAction, CouponQueryState } from './type'

function queryReducer(
  state: CouponQueryState,
  action: CouponQueryAction
): CouponQueryState {
  switch (action.type) {
    case 'updatePlatform':
      return {
        ...state,
        platform: action.payload,
      }

    case 'updateStore':
      return {
        ...state,
        store: action.payload,
      }

    default:
      return {
        ...state,
      }
  }
}

export default queryReducer

